import { ChangeDetectorRef, Component, OnDestroy, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, SelectControlValueAccessor, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { MessageService } from 'primeng/api';
import { Observable, Subject, takeUntil } from 'rxjs';
import { Member } from '../models/member';
import { TastingGroup } from '../models/tasting-group';
import { MemberService } from '../services/member.service';
import { TastingGroupService } from '../services/tasting-group.service';

@Component({
  selector: 'app-groups',
  templateUrl: './groups.component.html',
  styleUrls: ['./groups.component.scss']
})
export class GroupsComponent implements OnInit, OnDestroy {

  tastingGroups!: Array<TastingGroup>;
  groups$!: Observable<TastingGroup[]>;
  showDetails = false;
  showAddGroup = false;
  showMemberModal = false;
  modalHeader = '';
  memberModalHeader = '';
  isEdit = false;
  isEditMember = false;
  selectedGroupForEdit!: TastingGroup;
  selectedMemberForEdit!: Member;
  addGroupForm!: FormGroup;
  addMemberForm!: FormGroup;
  whiskeyOrganization = ['Scotch', 'Bourbon', 'Rye'];
  ngDestroyed$: Subject<boolean> = new Subject();

  constructor(private readonly router: Router,
              private readonly groupsService: TastingGroupService,
              private readonly messageService: MessageService,
              private readonly changeDetector: ChangeDetectorRef,
              private readonly fb: FormBuilder,
              private readonly memberService: MemberService) { }

  ngOnInit(): void {
    this.groups$ = this.groupsService.getAllTastingGroups();
    console.log(this.groups$);
    this.createAddGroupForm();
    this.createMemberForm();
  }

  ngOnDestroy(): void {
    this.ngDestroyed$.next(true);
  }

  addGroup() {
    this.showAddGroup = true;
    this.isEdit = false;
    this.modalHeader = 'Add Group';
  }

  addMember(groups: TastingGroup) {
    this.selectedGroupForEdit = groups;
    this.showMemberModal = true;
    this.isEditMember = false;
    this.memberModalHeader = 'Add Member';
  }

  createMemberForm() {
    this.addMemberForm = new FormGroup({
    MemberName: new FormControl(null, [Validators.required]),
    MemberEmail: new FormControl(null, [Validators.required, Validators.email]),
    MemberPhone: new FormControl(null, [Validators.required])
  }, { updateOn: 'blur' });
  }

  createAddGroupForm() {
    this.addGroupForm = new FormGroup({
      GroupName: new FormControl(null, [Validators.required]),
      SponsorName: new FormControl(null, [Validators.required]),
      SponsorPhone: new FormControl(null, [Validators.required]),
      SponsorEmail: new FormControl(null),
      OrganizationName: new FormControl(null),
      MaxGroupSize: new FormControl(null, [Validators.required])
    }, { updateOn: 'blur' });
  }

  editGroup(groups: TastingGroup) {
    this.isEdit = true;
    this.showAddGroup = true;
    this.selectedGroupForEdit = groups;
    this.addGroupForm.patchValue(this.selectedGroupForEdit);
    this.modalHeader = 'Edit Group Details';
  }

  editMember(member: Member) {
    this.isEditMember = true;
    this.showMemberModal = true;
    this.selectedMemberForEdit = member;
    this.addMemberForm.patchValue(this.selectedMemberForEdit);
    this.memberModalHeader = 'Edit Member Details'
  }

  onSubmit() {
    if (this.addGroupForm.valid) {
      if (!this.isEdit) {
        this.groupsService.addTastingGroup(this.addGroupForm.getRawValue()).pipe(takeUntil(this.ngDestroyed$)).subscribe(() => {
          this.groupsService.getAllTastingGroups();
          this.messageService.add({
            severity: 'success',
            summary: 'Group Added',
            detail:`${this.addGroupForm.getRawValue().GroupName} has been successfully added!`
          });
        });
        this.showAddGroup = false;
        this.changeDetector.detectChanges();
      } else {
        const editableGroup = this.addGroupForm.getRawValue();
        editableGroup.GroupId = this.selectedGroupForEdit.GroupId;
        this.groupsService.editTastingGroup(editableGroup).pipe(takeUntil(this.ngDestroyed$)).subscribe(() => {
          this.groupsService.getAllTastingGroups();
          this.messageService.add({
            severity: 'success',
            summary: 'Tasting Group Updated',
            detail:`${this.addGroupForm.getRawValue().GroupName} has been successfully updated!`
          });
          this.showAddGroup = false;
          this.changeDetector.detectChanges();
        })
      }
    }
  }

  onSubmitMember() {
    if(this.addMemberForm.valid) {
      if (!this.editMember) {
        this.memberService.addMemberToTastingGroup(this.selectedGroupForEdit.GroupId, this.addMemberForm.getRawValue()).pipe(takeUntil(this.ngDestroyed$)).subscribe(() => {
          this.groupsService.getAllTastingGroups();
          this.messageService.add({
            severity: 'success',
            summary:'Member Added',
            detail: `${this.addMemberForm.getRawValue().MemberName} has been successfully added to ${this.selectedGroupForEdit.GroupName}!`
          });
        });
        this.showMemberModal = false;
      }
    }
  }

  deleteGroup(groups: TastingGroup) {
    this.groupsService.deleteTastingGroup(groups.GroupId);
    console.log(groups.GroupId);
    this.messageService.add({
      severity: 'success',
      summary: 'Group Deleted',
      detail:`${groups.GroupName} has been successfully deleted.`
    });
    this.changeDetector.detectChanges();
  }

  deleteMember(member: Member) {

  }
}
<p-toast></p-toast>
<p-panel header="Tasting Groups">
  <p-table *ngIf="groups$ | async as groups"
           [value]="groups"
           dataKey="GroupName"
           selectionMode="single">
    <ng-template pTemplate="header">
      <tr>
        <th style="width: 3rem"></th>
        <th>Group Name</th>
        <th>Whiskey</th>
        <th>Sponsor</th>
        <th>Member Count</th>
        <th></th>
      </tr>
    </ng-template>
    <ng-template pTemplate="body" let-groups let-expanded="expanded">
      <tr>
        <td>
          <button type="button" pButton pRipple [pRowToggler]="groups" class="p-button-text p-button-rounded p-button-plain" [icon]="expanded ? 'pi pi-chevron-down' : 'pi pi-chevron-right'"></button>
      </td>
        <td>{{ groups.GroupName }}</td>
        <td>{{ groups.OrganizationName }}</td>
        <td>{{ groups.SponsorName }}</td>
        <td>{{ groups.Members.length }}</td>
        <td>
          <button *ngIf="!expanded" pButton pRipple icon="pi pi-pencil" class="p-button-rounded p-button-success mr-2" (click)="editGroup(groups)"></button>
          <button *ngIf="!expanded" pButton pRipple icon="pi pi-trash" class="p-button-rounded p-button-danger" (click)="deleteGroup(groups)"></button>
        </td>
      </tr>
    </ng-template>
    <ng-template pTemplate="rowexpansion" let-groups>
      <tr>
        <td colspan="7">
          <div class="p-3">
            <p-table [value]="groups.Members" dataKey="MemberName">
              <ng-template pTemplate="header">
                <tr>
                  <th>Member Name</th>
                  <th>Member Email</th>
                  <th>Member Phone</th>
                  <th></th>
                </tr>
              </ng-template>
              <ng-template pTemplate="body" let-member>
                <tr>
                  <td>{{ member.MemberName }}</td>
                  <td>{{ member.MemberEmail }}</td>
                  <td>{{ member.MemberPhone }}</td>
                  <td>
                    <button pButton pRipple icon="pi pi-pencil" class="p-button-rounded p-button-success mr-2" (click)="editMember(member)"></button>
                    <button pButton pRipple icon="pi pi-trash" class="p-button-rounded p-button-danger" (click)="deleteMember(member)"></button>
                  </td>
                </tr>
              </ng-template>
              <ng-template pTemplate="footer">
                <p-button class="add-member-btn" label="Add Member" *ngIf="!showDetails" (onClick)="addMember(groups)"></p-button>
              </ng-template>
            </p-table>
          </div>
        </td>
      </tr>
    </ng-template>
  </p-table>
  <p-button label="Add Tasting Group" *ngIf="!showDetails" (onClick)="addGroup()">

  </p-button>
</p-panel>
<div *ngIf="showAddGroup">
  <p-dialog [(visible)]="showAddGroup" [modal]="true" styleClass="p-fluid">
    <p-header>{{ modalHeader }}</p-header>
    <ng-template pTemplate="content">
      <form [formGroup]="addGroupForm">
        <div class="field">
          <label for="GroupName">Group Name</label>
          <input type="text"
                 pInputText
                 required
                 formControlName="GroupName">
        </div>
        <div class="field">
          <label>Sponsor Name</label>
          <input type="text"
                 pInputText
                 required
                 formControlName="SponsorName">
        </div>
        <div class="field">
          <label>Sponsor Phone</label>
          <input type="text"
                 pInputText
                 required
                 formControlName="SponsorPhone">
        </div>
        <div class="field">
          <label>Sponsor Email</label>
          <input type="phone"
                 pInputText
                 formControlName="SponsorEmail">
        </div>
        <div class="field">
          <label>Organization Name</label>
          <p-dropdown [options]="whiskeyOrganization"
                      placeholder="Select an organization"
                      formControlName="OrganizationName"></p-dropdown>
        </div>
        <div class="field">
          <label>Max Group Size</label>
          <p-inputNumber formControlName="MaxGroupSize"></p-inputNumber>
        </div>
      </form>
    </ng-template>
    <ng-template pTemplate="footer">
      <button pButton pRipple label="Cancel" icon="pi pi-times" class="p-button-text" (click)="showAddGroup = false"></button>
      <button pButton pRipple label="Save" icon="pi pi-check" class="p-button-text" (click)="onSubmit()"></button>
    </ng-template>
  </p-dialog>
</div>
<div *ngIf="showMemberModal">
  <p-dialog [(visible)]="showMemberModal" [modal]="true" styleClass="p-fluid">
    <p-header>{{ memberModalHeader }}</p-header>
    <ng-template pTemplate="content">
      <form [formGroup]="addMemberForm">
        <div class="field">
          <label for="MemberName">Member Name</label>
          <input type="text"
                 pInputText
                 required
                 formControlName="MemberName">
        </div>
        <div class="field">
          <label>Member Email</label>
          <input type="text"
                 pInputText
                 required
                 formControlName="MemberEmail">
        </div>
        <div class="field">
          <label>Member Phone</label>
          <input type="text"
                 pInputText
                 required
                 formControlName="MemberPhone">
        </div>
      </form>
    </ng-template>
    <ng-template pTemplate="footer">
      <button pButton pRipple label="Cancel" icon="pi pi-times" class="p-button-text" (click)="showMemberModal = false"></button>
      <button pButton pRipple label="Save" icon="pi pi-check" class="p-button-text" (click)="onSubmitMember()"></button>
    </ng-template>
  </p-dialog>

</div>
